<!--
<form class="some_form" enctype="multipart/form-data" method="post" action="">
<input type="file" name="photo" id="photoInput" />
<input type="submit">
</form>
<div id="imgContainer"></div>

<script type="text/javascript">
$.validator.addMethod('minImageSize', function(value, element, minSize) {
var imageSize = $(element).data('imageSize');
return (imageSize)
&& (imageSize.width > minSize.width)
&& (imageSize.height > minSize.height);
}, function(minSize, element) {
return ($(element).data('imageSize'))
? ("Your image's size must be greater than "
+ minSize.width + "px by " + minSize.height + "px")
: "Selected file is not an image.";
});

var validator = $('.some_form').validate({
rules: {
photo: {
required: true,
minImageSize: { width: 300, height: 500 }
}
},
messages: {
photo: {
required: "You must insert an image"
},
}
});

var $submitBtn = $('.some_form').find('input:submit'),
$photoInput = $('#photoInput'),
$imgContainer = $('#imgContainer');

$('#photoInput').change(function() {
$photoInput.removeData('imageSize');
$imgContainer.hide().empty();

var file = this.files[0];

if (file.type.match(/image\/.*/)) {
$submitBtn.attr('disabled', true);

var reader = new FileReader();

reader.onload = function() {
var $img = $('<img />').attr({ src: reader.result });

$img.on('load', function() {
$imgContainer.append($img).show();

$photoInput.data('imageSize', {
width: $img.width(),
height: $img.height()
});

$img.css({ width: '400px', height: '200px' });

$submitBtn.attr('disabled', false);

validator.element($photoInput);
});
}

reader.readAsDataURL(file);
} else {
validator.element($photoInput);
}
});


</script> -->
